mapper.select:
select
id,servName,prodId,servAddr,accsNbr,ty,speed,startdt,prodTel,bipDataNum,bipDuration,area,rsrc,itvProdId,packName,kpiProdId,phone,wareName,gridName,own,deep,state,chName
from
${table}


		deep = #{user.deep}


		and area = #{user.area}


		and chName = #{user.chName}


order by area,id
limit #{start},#{size}

select ifnull(count(1),0) from ${table}


		deep = #{user.deep}


		and area = #{user.area}


		and chName = #{user.chName}

mapper.update:
update ${table} set
state = #{state},
stateDate = #{stateDate}
where id = #{id}

UPDATE ${table}


		own = #{user.uid}, 


		deep =#{user.deep},    


		chName=#{user.chName}   


WHERE id IN

	#{item}
